import React, { useState } from 'react';
import Modal from '../atoms/Modal';
import Input from '../atoms/Input';
import Button from '../atoms/Button';
import { Eye, EyeOff } from 'lucide-react';
import { User } from '../../services/authService';

interface EditProfileModalProps {
    user: User;
    onClose: () => void;
}

const EditProfileModal: React.FC<EditProfileModalProps> = ({ user, onClose }) => {
    const [username, setUsername] = useState(user.username);
    const [email, setEmail] = useState(user.email);
    const [firstName, setFirstName] = useState(user.firstName || '');
    const [lastName, setLastName] = useState(user.lastName || '');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => setShowPassword((prev) => !prev);

    const handleSave = () => {
        // API call or callback
        console.log({ username, email, firstName, lastName, password });
        onClose();
    };

    return (
        <Modal isOpen={true} onClose={onClose} title="Modifier mon profil">
            <div className="space-y-4">
                <Input label="Nom d'utilisateur" value={username} onChange={(e) => setUsername(e.target.value)} />
                <Input label="PrÃ©nom" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
                <Input label="Nom" value={lastName} onChange={(e) => setLastName(e.target.value)} />
                <Input label="Email" value={email} onChange={(e) => setEmail(e.target.value)} />

                <div className="relative">
                    <Input
                        label="Nouveau mot de passe"
                        type={showPassword ? 'text' : 'password'}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button
                        type="button"
                        onClick={togglePasswordVisibility}
                        className="absolute right-3 bottom-2 text-neutral-500"
                        tabIndex={-1}
                    >
                        {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                    </button>
                </div>

                <div className="flex justify-end gap-3 pt-2">
                    <Button variant="secondary" onClick={onClose}>Annuler</Button>
                    <Button onClick={handleSave}>Modifier</Button>
                </div>
            </div>
        </Modal>
    );
};

export default EditProfileModal;
