import React, { useState } from 'react';
import Modal from '../atoms/Modal';
import Input from '../atoms/Input';
import Button from '../atoms/Button';
import { useAuth } from '../../contexts/AuthContext';

interface EditProfileModalProps {
    isOpen: boolean;
    onClose: () => void;
    onSave: (data: { username: string; email: string; password?: string }) => void;
}

const EditProfileModal: React.FC<EditProfileModalProps> = ({ isOpen, onClose, onSave }) => {
    const { user } = useAuth();

    const [username, setUsername] = useState(user?.username || '');
    const [email, setEmail] = useState(user?.email || '');
    const [password, setPassword] = useState('');

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSave({ username, email, password: password || undefined });
        onClose();
    };

    const handleCancel = () => {
        onClose();
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose} title="Modifier mon profil">
            <form onSubmit={handleSubmit} className="space-y-4">
                <Input
                    label="Nom d'utilisateur"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <Input
                    label="Email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <Input
                    label="Mot de passe (laissez vide pour ne pas changer)"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <div className="flex justify-end gap-2 pt-4">
                    <Button variant="secondary" onClick={handleCancel} type="button">
                        Annuler
                    </Button>
                    <Button type="submit">Modifier</Button>
                </div>
            </form>
        </Modal>
    );
};

export default EditProfileModal;
